mapper.select:
select f.id, f.user_id as userId,f.order_id as orderId,f.merchant_id as merchantId, f.item_id as itemId,f.pid ,f.evaluate,f.goodsScore,f.foodScore, f.distributionScore,f.created_at as createdAt,f.content from tb_feedbacks f where 1=1
and f.merchant_id = #{merchantId,jdbcType=VARCHAR}
order by f.id desc

		select sum(goodsScore)/count(0) as totalScore
		from  tb_feedbacks f
 		where 1=1 
		
			and f.merchant_id = #{merchantId,jdbcType=VARCHAR}
		
 		
			and f.goodsScore = #{goodsScore,jdbcType=VARCHAR}
		
		group by f.merchant_id
