mapper.select:
select u.*, (select count(0) from tb_orders o where o.user_id=u.id) as orderCount, (select sum(o.total_price) from tb_orders o where o.user_id=u.id and o.is_pay=1) as totalPrice from tb_users u where u.status=1
and u.userPhone = #{userPhone,jdbcType=VARCHAR}
and u.userName = #{userName,jdbcType=VARCHAR}
order by u.id desc

mapper.update:
update tb_users
set status = -1
where id = #{id,jdbcType=VARCHAR}
