mapper.select:
select

from users 
where username = #{username,jdbcType=VARCHAR} and md5_password = #{md5Password,jdbcType=VARCHAR}

     select 
     u.*,j.title as jailName,r.role_name as roleName
     from users u left join jails j on u.jail_id=j.id LEFT JOIN tb_sysrole r on u.role_id=r.id 
     where 1=1 and u.is_delete=0 
     
	     and u.role_id = #{roleId,jdbcType=VARCHAR} 
	 
	
	     and u.status = #{status,jdbcType=VARCHAR} 
	 
   	 
	     and u.login_name like '%' || #{loginName,jdbcType=VARCHAR} || '%'
	 
   	 
	     and u.created_at >= #{startTime,jdbcType=VARCHAR} 
	 
	 
	     and u.created_at <= #{endTime,jdbcType=VARCHAR} 
	 
      order by u.id desc

mapper.update:
update users
set is_delete = 1
where id = #{id,jdbcType=VARCHAR}
